package com.jacquis.jacquis_system.model;

import jakarta.persistence.Entity;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;
import jakarta.persistence.Table;
import jakarta.validation.constraints.Email;
import jakarta.validation.constraints.NotEmpty;
import jakarta.validation.constraints.NotNull;


@Entity
@Table(name = "empleado")
public class Empleado {
    
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id_empleado;

    @NotEmpty
    private String nombre;

    @NotEmpty
    private String apellido;
    private String apellido_dos;

    @NotEmpty
    private String direccion;

    @NotEmpty
    @Email
    private String correo_empleado;

    @NotNull
    private boolean estado_empleado;
    private String empleado_fk;
    // private salario salario;

    // PARA UNA FECHA
    // @NotNull
    // @Temporal(TemporalType.DATE)
    // DateTimeFormat(pattern = "dd/MM/yyyy")
    // private Date fecha;

    // Constructor
    public Empleado() {
    }

    // Getters y Setters

    public Long getId_empleado() {
        return id_empleado;
    }

    public void setId_empleado(Long id_empleado) {
        this.id_empleado = id_empleado;
    }

    public String getNombre() {
        return nombre;
    }

    public void setNombre(String nombre) {
        this.nombre = nombre;
    }

    public String getApellido() {
        return apellido;
    }

    public void setApellido(String apellido) {
        this.apellido = apellido;
    }

    public String getApellido_dos() {
        return apellido_dos;
    }

    public void setApellido_dos(String apellido_dos) {
        this.apellido_dos = apellido_dos;
    }

    public String getDireccion() {
        return direccion;
    }

    public void setDireccion(String direccion) {
        this.direccion = direccion;
    }

    public String getCorreo_empleado() {
        return correo_empleado;
    }

    public void setCorreo_empleado(String correo_empleado) {
        this.correo_empleado = correo_empleado;
    }

    public boolean isEstado_empleado() {
        return estado_empleado;
    }

    public void setEstado_empleado(boolean estado_empleado) {
        this.estado_empleado = estado_empleado;
    }

    public String getEmpleado_fk() {
        return empleado_fk;
    }

    public void setEmpleado_fk(String empleado_fk) {
        this.empleado_fk = empleado_fk;
    }
    
}
